import { configureStore } from '@reduxjs/toolkit';
import userSlice from './slices/userSlice';
import restaurantsSlice from './slices/restaurantsSlice';

export default configureStore({
    reducer: {
        user: userSlice,
        restaurants: restaurantsSlice,
    },
})
