import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    total: [],
}

const totalSlice = createSlice({
    name: 'total',
    initialState,
    reducers: {
        setTotal: (state, action) => {
            state.total = action.payload
        },
    }
});

export const { setRestaurants } = totalSlice.actions;

export default totalSlice.reducer;
