                </nav> */}
                <h1>Cosmos Pizza</h1>
            </MenuColumn>
            <TotalColumn></TotalColumn>
        </OrderDiv>
    )
}

const OrderDiv = styled.div`
    display: flex;
    width: 100vw;
`

const RestColumn = styled.div`
    width: 25%;
    background: #f2f2f2;
    box-shadow: 7px 7px 9px #d7d7d7, -7px -7px 9px #ffffff;

    display: flex;
    flex-direction: column;
    align-items: center;

    overflow-y: scroll;

    img {
        width: 100px;
        height: 100px;
        margin-top: 10px;
        filter: drop-shadow(-3px -3px 8px #6FD6FF);
    }

    input {
        width: 80%;
        height: 40px;
        margin-top: 15px;
        margin-bottom: 15px;
        border: none;
        border-radius: 12px;
        background: #f2f2f2;
        box-shadow: 7px 7px 9px #d7d7d7, -7px -7px 9px #ffffff;
        padding-left: 10px;

        &:focus {
            outline: none;
        }

        &::placeholder {
            font-family: "Barlow Condensed", sans-serif;
            text-transform: uppercase;
            font-weight: 400;
            letter-spacing: 1.5px;
        }
    }
`

const RestCard = styled.div`
    display: flex;
    width: 80%;
    height: 120px;
    border: 2px solid #6FD6FF;
    border-radius: 12px;
    background: #f2f2f2;
    box-shadow: 7px 7px 9px #d7d7d7, -7px -7px 9px #ffffff;
    margin: 5px 0px;
    padding: 0px 5px;

    &:hover {
        transform: scale(1.03);
        transition: all 0.3s ease-in-out;
        cursor: pointer;
      }

    transition: all 0.3s ease-in-out;

`

const MenuColumn = styled.div`
    width: 50%;
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    overflow-y: scroll;

    nav {
        display: flex;
        flex-direction: row;
    }
`

const TotalColumn = styled.div`
    width: 25%;
    background: #f2f2f2;
    box-shadow: 7px 7px 9px #d7d7d7, -7px -7px 9px #ffffff;
`

export default Order;
