import { createSlice } from '@reduxjs/toolkit';

const initialState = []
const cartSlice = createSlice({
    name: 'cart',
    initialState,
    reducers: {
        setCart: (state, action) => {
            const example = state.find(item => {
               if(item.name === action.payload.name) {
                   item.quantity += 1;
               }
               return item.name === action.payload.name;
            })
            if(!example) {
                state.push(action.payload)
            }
        },
        updateCart: (state, action) => {
            return state.filter(item => item.name !== action.payload.name)
        },
        clearCart: (state, action) => {
            return initialState;
        },
        getTotal: (state, action) => {
            // filter through state array and go into each object
            let runningTotal = 0;
            state.forEach(item => {
                const itemPrice = item.price;
                const itemQuantity = item.quantity;
                runningTotal += (itemPrice * itemQuantity);
            })
            return runningTotal;
            // keep a running tally of state.price
            // save to other variable, aka const itemPrice = state.item.price;
            // multiply by const itemQuantity = state.item.quantity
            // for every item, const total += (itemQuantity * itemPrice);
            // return total
        }
    }
});

export const { setCart, updateCart, clearCart, getTotal } = cartSlice.actions;

export default cartSlice.reducer;
