import React, { useEffect } from 'react'
import styled from 'styled-components';
import { useSelector, useDispatch } from 'react-redux';
import { setRestaurants } from '../slices/restaurantsSlice';

function Order() {
    const dispatch = useDispatch();
    const restaurants = useSelector(state => state.restaurants);

    useEffect(() => {
        fetch('https://www.googleapis.com/geolocation/v1/geolocate?key=AIzaSyBIY2p0W20AYqFY9vEzAHiB-lf9uJx4rF0', { method: 'POST' })
            .then(response => response.json())
            .then(result => fetch(`https://api.documenu.com/v2/restaurants/search/geo?lat=${result.location.lat}&lon=${result.location.lng}&distance=5&key=83f2b023d3c913637c5ec321c4047a0e`))
            .then(response => response.json())
            .then(res => dispatch(setRestaurants(res.data)))
            .catch(err => console.log(err))
    }, [])

    return (
        <OrderDiv>
            <RestColumn>
                <img src="/assets/FoodDeliveryApp.png" alt="Food Delivery App Logo"/>
                <input type="text" placeholder="Search Restaurants"/>
                {restaurants && restaurants.map(restaurant => {
                    return (<div>{restaurant.restaurant_name}</div>)
                })}
            </RestColumn>
            <MenuColumn></MenuColumn>
            <TotalColumn></TotalColumn>
        </OrderDiv>
    )
}

const OrderDiv = styled.div`
    display: flex;
    width: 100vw;
`

const RestColumn = styled.div`
    width: 25%;
    background: #f2f2f2;
    box-shadow: 7px 7px 9px #d7d7d7, -7px -7px 9px #ffffff;

    display: flex;
    flex-direction: column;
    align-items: center;

    img {
        width: 100px;
        height: 100px;
        margin-top: 10px;
        filter: drop-shadow(-3px -3px 8px #6FD6FF);
    }

    input {
        width: 80%;
        height: 30px;
        margin-top: 15px;
        border: none;
        border-radius: 12px;
        background: #f2f2f2;
        box-shadow: 7px 7px 9px #d7d7d7, -7px -7px 9px #ffffff;
        padding-left: 10px;

        &:focus {
            outline: none;
        }

        &::placeholder {
            font-family: "Barlow Condensed", sans-serif;
            text-transform: uppercase;
            font-weight: 400;
            letter-spacing: 1.5px;
        }
    }
`

const MenuColumn = styled.div`
    width: 50%;
`

const TotalColumn = styled.div`
    width: 25%;
    background: #f2f2f2;
    box-shadow: 7px 7px 9px #d7d7d7, -7px -7px 9px #ffffff;
`

export default Order;
