import { createSlice, current } from '@reduxjs/toolkit';


const initialState = []
const cartSlice = createSlice({
    name: 'cart',
    initialState,
    reducers: {
        setCart: (state, action) => {
            console.log(current(state))
            console.log(current(action.payload))
            if(state.includes(action.payload.name)) {
                const increase = state.find(item => item.name === action.payload.name);
                console.log(current(increase));
                if(increase) {
                    increase.quantity += 1;
                }
            } else {
                state.push(action.payload)
            }
        },
        updateCart: (state, action) => {
            return state.filter(item => item.name !== action.payload.name)
        },
        clearCart: (state, action) => {
            return initialState;
        }
    }
});

export const { setCart, updateCart, clearCart } = cartSlice.actions;

export default cartSlice.reducer;
