import React, { useState } from 'react';
import styled from 'styled-components';

import { Map, GoogleApiWrapper, InfoWindow, Marker } from 'google-maps-react';

const mapStyles = {
    width: '100%',
    height: '50'
};

let initialState = {
    showingInfoWindow: false,  // Hides or shows the InfoWindow
    activeMarker: {},          // Shows the active marker upon click
    selectedPlace: {}          // Shows the InfoWindow to the selected place upon a marker
};

function Confirmation(props) {
    const [state, setState] = useState(initialState);

    const onMarkerClick = (props, marker, e) => {
        setState({
          selectedPlace: props,
          activeMarker: marker,
          showingInfoWindow: true
        });
    }

    const onClose = props => {
        if (this.state.showingInfoWindow) {
          this.setState({
            showingInfoWindow: false,
            activeMarker: null
          });
        }
    };
    return (
        <div>
            <ConfirmationTop>
                <img src="/assets/FoodDeliveryApp.png" alt="Food Delivery App Logo"/>
                <h1>confirmation</h1>
            </ConfirmationTop>
            <ConfirmationBottom>
                <Map
                    google={props.google}
                    zoom={15}
                    style={mapStyles}
                    initialCenter={
                    {
                        lat: 40.014,
                        lng: -105.269
                    }
                    }
                >
                    <Marker
                    name={'Mexican'}
                    onClick={onMarkerClick(this)}
                    position={{lat: 40.016, lng: -105.282}} />
                    <Marker
                    name={'Pizza Pizza'}
                    onClick={onMarkerClick}
                    position={{lat: 40.011, lng: -105.279}} />
                    <Marker />

                    <Marker
                    onClick={onMarkerClick}
                    name={'Freddies Hot Dogs'}
                    />
                    <InfoWindow
                    marker={state.activeMarker}
                    visible={state.showingInfoWindow}
                    onClose={onClose}
                    >
                    <div>
                        <h1>*Food Truck*</h1>
                        <h3>{state.selectedPlace.name}</h3>
                    </div>
                    </InfoWindow>
                </Map>
            </ConfirmationBottom>
        </div>
    )
}

const ConfirmationTop = styled.div`

`

const ConfirmationBottom = styled.div`

`

export default GoogleApiWrapper({
    apiKey: 'AIzaSyDG2yQUqLUTXWRPyDceJqntNldd_v69-Aw'
  })(Confirmation);
