import React, { useState } from 'react';
import styled, { keyframes } from 'styled-components';

import Login from './components/LogIn';
import SignUp from './components/SignUp';

function App() {
  const [login, setLogin] = useState(true);

  return (
    <AppDiv>
      <Contact><ContactH2>Contact Us</ContactH2></Contact>
      <Logo><img src="/assets/FoodDeliveryApp.png" alt="Food Delivery App Logo"/></Logo>
      <HeroBackground>
        <LoginSignUp>
          <LogInH2 onClick={() => setLogin(true)}>Log In</LogInH2>
          <SignUpH2 onClick={() => setLogin(false)}>Sign Up</SignUpH2>
        </LoginSignUp>
        {login ? <Login/> : <SignUp/>}
        <Divider>
          <hr/>
          <h3>OR</h3>
        </Divider>

      </HeroBackground>
    </AppDiv>
  );
}


const kf = keyframes`
  100% {
    opacity: 1;
  }
`

const AppDiv = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`
const Contact = styled.div`
  opacity: 0;
  animation: ${kf} 0.5s ease-in-out forwards;
  display: flex;
  align-items: center;
  text-align: center;
  width: 175px;
  height: 50px;
  margin: 50px 0px;
  margin-right: -500px;
  border-radius: 18px;
  background: #f2f2f2;
  box-shadow: 7px 7px 9px #d7d7d7, -7px -7px 9px #ffffff;

  &:hover {
    transform: scale(1.05);
    transition: all 0.3s ease-in-out;
    cursor: pointer;
  }
  transition: all 0.3s ease-in-out;
`

const ContactH2 = styled.h2`
  padding-top: 15px;
  text-transform: uppercase;
  font-weight: 400;
`
const Logo = styled.div`
  opacity: 0;
  animation: ${kf} 0.5s ease-in-out forwards;
  width: 300px;
  height: 300px;
  position: absolute;
  z-index: 1;
  margin-top: 50px;
`

const HeroBackground = styled.div`
  opacity: 0;
  animation: ${kf} 0.5s ease-in-out forwards;
  width: 650px;
  position: relative;
  height: 450px;
  margin-top: 50px;
  border-radius: 38px;
  background: #f2f2f2;
  box-shadow: 7px 7px 9px #d7d7d7, -7px -7px 9px #ffffff;
`

const LoginSignUp = styled.div`
  display: flex;
  text-align: center;
  width: 100%;
  height: 100px;
  padding-top: 20px;
`

const SignUpH2 = styled.h2`
  text-transform: uppercase;
  font-weight: 400;
  padding-left: 200px;
  &:hover {
    transform: scale(1.03);
    transition: all 0.3s ease-in-out;
    cursor: pointer;
  }
  transition: all 0.3s ease-in-out;
`
const LogInH2 = styled.h2`
  text-transform: uppercase;
  font-weight: 400;
  &:hover {
    transform: scale(1.04);
    transition: all 0.3s ease-in-out;
    cursor: pointer;
  }
  transition: all 0.3s ease-in-out;
`

const Divider = styled.div`
  display: flex;

  hr {
    height: 1px;
  }
`

export default App;
